pragma solidity ^0.8.0;

import {DateTime} from "./DateTime.sol";

struct ParsedSiweMessage {
    bytes schemeDomain;
    address addr;
    bytes statement;
    bytes uri;
    bytes version;
    uint256 chainId;
    bytes nonce;
    bytes issuedAt;
    bytes expirationTime;
    bytes notBefore;
    bytes requestId;
    bytes[] resources;
}

/**
 * @title On-chain parser for EIP-4361 SIWE message
 * @notice Call parseSiweMsg() and provide the EIP-4361 SIWE message. The parser
 * will generate the ParsedSiweMessage struct which you can then use to
 * extract the authentication information in your on-chain contract.
 */
library SiweParser {
    /// Invalid length of the hex-encoded address
    error SiweParser_InvalidAddressLength();
    /// Invalid length of the nonce
    error SiweParser_InvalidNonce();

    /**
     * @notice Convert string containing hex address without 0x prefix to solidity address object.
     */
    function _hexStringToAddress(bytes memory s)
        internal
        pure
        returns (address)
    {
        if (s.length != 40) {
            revert SiweParser_InvalidAddressLength();
        }

        bytes memory r = new bytes(s.length / 2);
        for (uint256 i = 0; i < s.length / 2; ++i) {
            r[i] = bytes1(
                _fromHexChar(uint8(s[2 * i])) *
                    16 +
                    _fromHexChar(uint8(s[2 * i + 1]))
            );
        }
        return address(bytes20(r));
    }

    function _fromHexChar(uint8 c) internal pure returns (uint8) {
        if (bytes1(c) >= bytes1("0") && bytes1(c) <= bytes1("9")) {
            return c - uint8(bytes1("0"));
        }
        if (bytes1(c) >= bytes1("a") && bytes1(c) <= bytes1("f")) {
            return 10 + c - uint8(bytes1("a"));
        }
        if (bytes1(c) >= bytes1("A") && bytes1(c) <= bytes1("F")) {
            return 10 + c - uint8(bytes1("A"));
        }
        return 0;
    }

    /**
     * @notice Substring.
     */
    function _substr(
        bytes memory str,
        uint256 startIndex,
        uint256 endIndex
    ) internal pure returns (bytes memory) {
        bytes memory result = new bytes(endIndex - startIndex);
        for (uint256 i = startIndex; i < endIndex && i < str.length; i++) {
            result[i - startIndex] = str[i];
        }
        return result;
    }

    /**
     * @notice String to Uint using decimal format. No error handling.
     */
    function _parseUint(bytes memory b) internal pure returns (uint256) {
        uint256 result = 0;
        for (uint256 i = 0; i < b.length; i++) {
            result = result * 10 + (uint256(uint8(b[i])) - 0x30);
        }
        return (result);
    }

    /**
     * @notice Parse "NAME: VALUE" in str starting at index i and ending at \n or end of bytes.
     * @return VALUE and new i, if NAME matched; otherwise empty value and old i.
     */
    function _parseField(
        bytes calldata str,
        string memory name,
        uint256 i
    ) internal pure returns (bytes memory, uint256) {
        uint256 j = i;
        for (; j < str.length; j++) {
            if (str[j] == ":") {
                // Delimiter found, check the name.
                if (keccak256(_substr(str, i, j)) != keccak256(bytes(name))) {
                    return ("", i);
                }

                // Skip :
                j++;
                if (j < str.length && str[j] == " ") {
                    // Skip blank
                    j++;
                }

                i = j;
                break;
            }
        }

        for (; j < str.length; j++) {
            if (str[j] == 0x0a) {
                return (_substr(str, i, j), j + 1);
            }
        }
        return (_substr(str, i, j), j);
    }

    /**
     * @notice Parse bullets, one per line in str starting at i.
     * @return Array of parsed values and a new i.
     */
    function _parseArray(bytes calldata str, uint256 i)
        internal
        pure
        returns (bytes[] memory, uint256)
    {
        // First count the number of resources.
        uint256 j = i;
        uint256 count = 0;
        for (; j < str.length - 1; j++) {
            if (str[j] == "-" && str[j + 1] == " ") {
                j += 2;
                count++;
            } else {
                break;
            }
            while (j < str.length && str[j] != 0x0a) {
                j++;
            }
        }

        // Then build an array.
        bytes[] memory values = new bytes[](count);
        j = i;
        for (uint256 c = 0; j < str.length - 1 && c != count; j++) {
            if (str[j] == "-" && str[j + 1] == " ") {
                i = j + 2;
            }
            while (j < str.length && str[j] != 0x0a) {
                j++;
            }
            values[c] = _substr(str, i, j);
            c++;
            if (j == str.length) {
                j--; // Subtract 1 because of the outer loop.
            }
        }
        return (values, j);
    }

    /**
     * @notice Parse SIWE message.
     * @return ParsedSiweMessage struct with populated fields from the message.
     */
    function parseSiweMsg(bytes calldata siweMsg)
        internal
        pure
        returns (ParsedSiweMessage memory)
    {
        ParsedSiweMessage memory p;
        uint256 i = 0;

        // dApp Domain.
        for (; i < siweMsg.length; i++) {
            if (siweMsg[i] == " ") {
                p.schemeDomain = _substr(siweMsg, 0, i);
                break;
            }
        }

        i += 50; // " wants you to sign in with your Ethereum account:\n"

        // Address.
        // TODO: Verify the mixed-case checksum.
        p.addr = _hexStringToAddress(_substr(siweMsg, i += 2, i += 40));
        i += 2; // End of address new line + New line.

        // (Optional) statement.
        if (i < siweMsg.length && siweMsg[i] != "\n") {
            for (uint256 j = i; j < siweMsg.length; j++) {
                if (siweMsg[j] == 0x0a) {
                    p.statement = _substr(siweMsg, i, j);
                    i = j + 1; // End of statement new line.
                    break;
                }
            }
        }

        i++; // New line.

        (p.uri, i) = _parseField(siweMsg, "URI", i);
        (p.version, i) = _parseField(siweMsg, "Version", i);
        bytes memory chainId;
        (chainId, i) = _parseField(siweMsg, "Chain ID", i);
        p.chainId = _parseUint(chainId);
        (p.nonce, i) = _parseField(siweMsg, "Nonce", i);
        if (p.nonce.length < 8) {
            revert SiweParser_InvalidNonce();
        }
        (p.issuedAt, i) = _parseField(siweMsg, "Issued At", i);
        (p.expirationTime, i) = _parseField(siweMsg, "Expiration Time", i);
        (p.notBefore, i) = _parseField(siweMsg, "Not Before", i);
        (p.requestId, i) = _parseField(siweMsg, "Request ID", i);

        // Parse resources, if they exist.
        uint256 newI;
        (, newI) = _parseField(siweMsg, "Resources", i);
        if (newI != i) {
            (p.resources, i) = _parseArray(siweMsg, newI);
        }

        return p;
    }

    /**
     * @notice Parse RFC 3339 (ISO 8601) string to timestamp.
     */
    function timestampFromIso(bytes memory str)
        internal
        pure
        returns (uint256)
    {
        return
            DateTime.toTimestamp(
                uint16(_parseUint(_substr(str, 0, 4))),
                uint8(_parseUint(_substr(str, 5, 7))),
                uint8(_parseUint(_substr(str, 8, 10))),
                uint8(_parseUint(_substr(str, 11, 13))),
                uint8(_parseUint(_substr(str, 14, 16))),
                uint8(_parseUint(_substr(str, 17, 19)))
            );
    }
}
","keccak256":"0x07529271c0beff34900dcd2d967635ddf0af86645a1c5fdcf7396b12bc3a9965","license":"Apache-2.0"},"@oasisprotocol/sapphire-contracts/contracts/auth/A13e.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import {SignatureRSV} from "../EthereumUtils.sol";

/**
 * @title Interface for authenticatable contracts
 * @notice This is the interface for universal authentication mechanism (e.g.
 * SIWE):
 * 1. The user-facing app calls `login()` which generates the authentication
 *    token on-chain.
 * 2. Any smart contract method that requires authentication can take this token
 *    as an argument. Passing this token to `authMsgSender()` verifies it and
 *    returns the **authenticated** user address. This verified address can then
 *    serve as a user ID for authorization.
 */
abstract contract A13e {
    /// A mapping of revoked authentication tokens. Access it directly or use the checkRevokedAuthToken modifier.
    mapping(bytes32 => bool) internal _revokedAuthTokens;

    /// The authentication token was revoked
    error A13e_RevokedAuthToken();

    /**
     * @notice Reverts if the given token was revoked
     */
    modifier checkRevokedAuthToken(bytes memory token) {
        if (_revokedAuthTokens[keccak256(token)]) {
            revert A13e_RevokedAuthToken();
        }
        _;
    }

    /**
     * @notice Verify the login message and its signature and generate the
     * token.
     */
    function login(string calldata message, SignatureRSV calldata sig)
        external
        view
        virtual
        returns (bytes memory);

    /**
     * @notice Validate the token and return authenticated msg.sender.
     */
    function authMsgSender(bytes memory token)
        internal
        view
        virtual
        returns (address);

    /**
     * @notice Revoke the authentication token with the corresponding hash.
     * e.g. In case when the token is leaked or for extra-secure apps on
     * every logout.
     */
    function revokeAuthToken(bytes32 token) internal {
        _revokedAuthTokens[token] = true;
    }
}
","keccak256":"0x0cf14dfe3264acfd5b67c0559d668dfbbdeea96879ade2c1144aac3931968fe2","license":"Apache-2.0"},"@oasisprotocol/sapphire-contracts/contracts/auth/SiweAuth.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";

import {SignatureRSV, A13e} from "./A13e.sol";
import {ParsedSiweMessage, SiweParser} from "../SiweParser.sol";
import {Sapphire} from "../Sapphire.sol";

/// @title AuthToken structure for SIWE-based authentication
struct AuthToken {
    string domain; // [ scheme "://" ] domain.
    address userAddr;
    uint256 validUntil; // in Unix timestamp.
    string statement; // Human-readable statement from the SIWE message.
    string[] resources; // Resources this token grants access to.
}

/**
 * @title Base contract for SIWE-based authentication
 * @notice Inherit this contract if you wish to enable SIWE-based
 * authentication in your contract functions that require authentication.
 * The smart contract needs to be bound to a domain (passed in constructor).
 *
 * #### Example
 *
 * ```solidity
 * contract MyContract is SiweAuth {
 *   address private _owner;
 *   string private _message;
 *
 *   modifier onlyOwner(bytes memory token) {
 *     if (msg.sender != _owner && authMsgSender(token) != _owner) {
 *       revert("not allowed");
 *     }
 *     _;
 *   }
 *
 *   constructor(string memory domain) SiweAuth(domain) {
 *     _owner = msg.sender;
 *   }
 *
 *   function getSecretMessage(bytes memory token) external view onlyOwner(token) returns (string memory) {
 *     return _message;
 *   }
 *
 *   function setSecretMessage(string calldata message) external onlyOwner("") {
 *     _message = message;
 *   }
 * }
 * ```
 */
contract SiweAuth is A13e {
    /// Domain which the dApp is associated with
    string internal _domain;
    /// Encryption key which the authentication tokens are encrypted with
    bytes32 private _authTokenEncKey;
    /// Default authentication token validity, if no expiration-time provided
    uint256 private constant DEFAULT_VALIDITY = 24 hours;

    /// Chain ID in the SIWE message does not match the actual chain ID
    error SiweAuth_ChainIdMismatch();
    /// Domain in the SIWE message does not match the domain of a dApp
    error SiweAuth_DomainMismatch();
    /// User address in the SIWE message does not match the message signer's address
    error SiweAuth_AddressMismatch();
    /// The Not before value in the SIWE message is still in the future
    error SiweAuth_NotBeforeInFuture();
    /// Validity of the authentication token or the Expires value in the SIWE message is in the past
    error SiweAuth_Expired();

    /**
     * @notice Instantiate the contract which uses SIWE for authentication and
     * runs on the specified domain.
     * @param inDomain The domain this contract is associated with
     */
    constructor(string memory inDomain) {
        _authTokenEncKey = bytes32(Sapphire.randomBytes(32, ""));
        _domain = inDomain;
    }

    /**
     * @notice Login using a SIWE message and signature
     * @param siweMsg The signed SIWE message
     * @param sig The signature of the SIWE message
     * @return The encrypted authentication token
     */
    function login(string calldata siweMsg, SignatureRSV calldata sig)
        external
        view
        override
        returns (bytes memory)
    {
        AuthToken memory b;

        // Derive the user's address from the signature.
        bytes memory eip191msg = abi.encodePacked(
            "\x19Ethereum Signed Message:\n",
            Strings.toString(bytes(siweMsg).length),
            siweMsg
        );
        address addr = ecrecover(
            keccak256(eip191msg),
            uint8(sig.v),
            sig.r,
            sig.s
        );
        b.userAddr = addr;

        ParsedSiweMessage memory p = SiweParser.parseSiweMsg(bytes(siweMsg));

        if (p.chainId != block.chainid) {
            revert SiweAuth_ChainIdMismatch();
        }

        if (keccak256(p.schemeDomain) != keccak256(bytes(_domain))) {
            revert SiweAuth_DomainMismatch();
        }
        b.domain = string(p.schemeDomain);

        if (p.addr != addr) {
            revert SiweAuth_AddressMismatch();
        }

        if (
            p.notBefore.length != 0 &&
            block.timestamp <= SiweParser.timestampFromIso(p.notBefore)
        ) {
            revert SiweAuth_NotBeforeInFuture();
        }

        if (p.expirationTime.length != 0) {
            // Compute expected block number at expiration time.
            uint256 expirationTime = SiweParser.timestampFromIso(
                p.expirationTime
            );
            b.validUntil = expirationTime;
        } else {
            // Otherwise, just take the default validity.
            b.validUntil = block.timestamp + DEFAULT_VALIDITY;
        }
        if (block.timestamp >= b.validUntil) {
            revert SiweAuth_Expired();
        }

        // Store statement from the SIWE message.
        b.statement = string(p.statement);

        // Store resources from the SIWE message.
        b.resources = new string[](p.resources.length);
        for (uint256 i = 0; i < p.resources.length; i++) {
            b.resources[i] = string(p.resources[i]);
        }

        bytes memory encB = Sapphire.encrypt(
            _authTokenEncKey,
            0,
            abi.encode(b),
            ""
        );
        return encB;
    }

    /**
     * @notice Return the domain associated with the dApp.
     * @return The domain string
     */
    function domain() public view returns (string memory) {
        return _domain;
    }

    /**
     * @notice Get the authenticated address from a token
     * @param token The authentication token
     * @return The authenticated user address or zero address if token is empty
     */
    function authMsgSender(bytes memory token)
        internal
        view
        override
        checkRevokedAuthToken(token)
        returns (address)
    {
        if (token.length == 0) {
            return address(0);
        }

        AuthToken memory b = decodeAndValidateToken(token);
        return b.userAddr;
    }

    /**
     * @notice Get the statement from the authentication token
     * @param token The authentication token
     * @return The statement string from the SIWE message
     */
    function getStatement(bytes memory token)
        internal
        view
        checkRevokedAuthToken(token)
        returns (string memory)
    {
        if (token.length == 0) {
            return "";
        }

        AuthToken memory b = decodeAndValidateToken(token);
        return b.statement;
    }

    /**
     * @notice Get all resources from the authentication token
     * @param token The authentication token
     * @return Array of resource URIs the token grants access to
     */
    function getResources(bytes memory token)
        internal
        view
        checkRevokedAuthToken(token)
        returns (string[] memory)
    {
        if (token.length == 0) {
            return new string[](0);
        }

        AuthToken memory b = decodeAndValidateToken(token);
        return b.resources;
    }

    /**
     * @notice Helper function to decrypt, decode and validate a token
     * @dev Performs token decoding as well as domain and validation
     * @param token The authentication token
     * @return The decoded and validated AuthToken struct
     */
    function decodeAndValidateToken(bytes memory token)
        internal
        view
        virtual
        returns (AuthToken memory)
    {
        bytes memory authTokenEncoded = Sapphire.decrypt(
            _authTokenEncKey,
            0,
            token,
            ""
        );
        AuthToken memory b = abi.decode(authTokenEncoded, (AuthToken));

        // Validate domain
        if (keccak256(bytes(b.domain)) != keccak256(bytes(_domain))) {
            revert SiweAuth_DomainMismatch();
        }

        // Validate expiry
        if (b.validUntil < block.timestamp) {
            revert SiweAuth_Expired();
        }

        return b;
    }
}
